import React from 'react'
import Layout from '../../../components/Layout';
import instance from '../../../ethereum/tender';
import web3 from '../../../ethereum/web3';
import {Form,Button,Message} from 'semantic-ui-react'
import {Router} from '../../../routes';
class register extends React.Component{
	state={
		account:''
	}
	onSubmit=async (event)=>{
		event.preventDefault();
		this.setState({loading:true,errorMessage:''});
		try{
			const accounts=await web3.eth.getAccounts();
			this.setState({account:accounts[0]});
			await instance.methods.Register(this.state.account)
			.send({
				from:accounts[0]
			})
			const obj=await instance.methods.BidRegisters(this.state.account).call();
			console.log(obj);
			Router.pushRoute('/')
			// Router.pushRoute(`/tenders/${this.props.tenderId}`);
		}catch(err){
			this.setState({errorMessage:err.message});
		}
		this.setState({loading:false})
	}
	render(){
		return(
			<Layout>
				<Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
					<Form.Field>
						<label>Paste Address</label>
						<input placeholder="address" value={this.state.account} onChange={event=>this.setState({account:event.target.value})}/>
					</Form.Field>


					<Message error header="Oops!!" content={this.state.errorMessage} />
					<Button loading={this.state.loading} size='large' primary type='submit'>Submit</Button>
				</Form>
			</Layout>
			)
	}		
}
export default register
